  const[showCart, setShowCart]=useState(false);

  const showCartHandler = ()=>{
    setShowCart(true);
  };
  const hideCartHandler =()=>{
    setShowCart(false);
  };
  return (
    <Fragment>
      { showCart && <Cart />}
      <Header />
      <main>
        <Meals />
      </main>
    </Fragment>
  );
};

export default App;
